inputs.updateStateMemoryCode:
const result = $flow.output;
const usedTools = result.usedTools ?? [];

// Check if correct tool is being used
const calledTool =  usedTools.find((usedTool) => usedTool.tool === "patient_identity_database");
if (!calledTool) return {};


try {
  const parsedToolOutput = JSON.parse(calledTool.toolOutput);
  return {
    userInfo: parsedToolOutput // parsing tool output since its always string
  };
} catch (e) {
  return {};
}
