const timeZone = 'Australia/Sydney';
const options = {
    timeZone: timeZone,
    year: 'numeric',
    month: 'long',
    day: 'numeric',
    weekday: 'long',
    hour: '2-digit',
    minute: '2-digit',
    second: '2-digit',
    hour12: true
};
const today = new Date();
const formattedDate = today.toLocaleString('en-GB', options);
const result = {
    "formattedDate": formattedDate,
    "timezone": timeZone
};
return JSON.stringify(result);
