const fetch = require('node-fetch');
const apiKey = '';  // Put your Perplexity API key here.

const query = $query;

const options = {
	method: 'POST',
	headers: {
		'Content-Type': 'application/json',
		'Authorization': `Bearer ${apiKey}`
	},
	body: JSON.stringify({
		model: 'llama-3.1-sonar-small-128k-online',  // Model
		messages: [
			{
				role: 'system',
				content: 'You are a market research assistant.'
			},
			{
				role: 'user',
				content: query
			}
		]
	})
};

try {
	console.log(`Sending request with query: ${query}`);
	const response = await fetch('https://api.perplexity.ai/chat/completions', options);
	const data = await response.json();

	if (!response.ok) {
		throw new Error(`API error: ${response.status} ${response.statusText} - ${JSON.stringify(data)}`);
	}

	console.log('API response:', data);
	return JSON.stringify(data);
} catch (error) {
	console.error('Error occurred while fetching data from Perplexity AI:', error);
	return `Error occurred while fetching data from Perplexity AI: ${error.message}`;
}
